def matchTitle(title, excludeList=[]):
    Y = np.zeros((1,len(voc)), np.int64)
    for word in title.split():
        word = regex.sub('', word).lower()
        if word in voc:
            wIdx = voc.index(word)
            Y[0][wIdx] += 1
        elif word not in stop_words and len(word)>1:
            print("'{}' not in vocabulary.".format(word))

    doc_topic = lda_model.transform(Y)
#    print("{} (top topic: {})".format(title, doc_topic.argmax()))
    return findMostMatch(doc_topic[0], excludeList)

def findMostMatch(doc_topic_to_match, excludeList):
    minError = 10000
    index = 0
    for exist_doc_topic in lda_model.doc_topic_:
        if index not in excludeList:
            error = distance(exist_doc_topic, doc_topic_to_match)
            if error < minError:
                minError = error
                matchedIndex = index
        index += 1

#    print(matchedIndex, minError, video_title_array[matchedIndex])
    excludeList.append(matchedIndex)
    return excludeList

def distance(tuple1, tuple2):
    cmpLen = min(len(tuple1), len(tuple2))
    error = 0.0
    for i in range(cmpLen):
        error += (tuple1[i]-tuple2[i])*(tuple1[i]-tuple2[i])
    return error

